css:
/* Posty Fresh: raised buttons with mint green tones */
:root {
    --posty-border: oklch(0.50 0.13 158);
    --posty-shelf: oklch(0.44 0.14 155);
}
.dark {
    --posty-border: oklch(0.45 0.10 158);
    --posty-shelf: oklch(0.40 0.11 155);
}
[data-flux-button][data-flux-group-target] {
    border: 1.5px solid var(--posty-border) !important;
    box-shadow: 0 2px 0 0 var(--posty-shelf);
    transform: translateY(-2px);
    top: 2px;
    font-weight: 700;
    position: relative;
}
[data-flux-button][data-flux-group-target]:hover {
    transform: translateY(-3px);
    box-shadow: 0 3px 0 0 var(--posty-shelf);
}
[data-flux-button][data-flux-group-target]:active {
    transform: translateY(-1.5px);
    box-shadow: 0 1.5px 0 0 var(--posty-shelf);
    transition: all 100ms ease;
}
[data-flux-button]:disabled {
    transform: none !important;
    box-shadow: none !important;
    top: 0 !important;
}
[data-flux-button-group] [data-flux-button] {
    box-shadow: none !important;
    transform: none !important;
    top: 0 !important;
}
