css:
/* Posty: PostHog-style raised buttons */
/* PostHog tokens: border #B17816 (rgb 177,120,22), shadow #CD8407, orange #EB9D2A */
/* dark: border #835C19, shadow #99660E */
:root {
    --posty-border: oklch(0.58 0.13 70);
    --posty-shelf: oklch(0.52 0.14 68);
}
.dark {
    --posty-border: oklch(0.47 0.10 70);
    --posty-shelf: oklch(0.42 0.11 68);
}
/* Match PostHog: translateY(-2px) + top:2px = shelf stays anchored */
[data-flux-button][data-flux-group-target] {
    border: 1.5px solid var(--posty-border) !important;
    box-shadow: 0 2px 0 0 var(--posty-shelf);
    transform: translateY(-2px);
    top: 2px;
    font-weight: 700;
    position: relative;
}
[data-flux-button][data-flux-group-target]:hover {
    transform: translateY(-3px);
    box-shadow: 0 3px 0 0 var(--posty-shelf);
}
[data-flux-button][data-flux-group-target]:active {
    transform: translateY(-1.5px);
    box-shadow: 0 1.5px 0 0 var(--posty-shelf);
    transition: all 100ms ease;
}
[data-flux-button]:disabled {
    transform: none !important;
    box-shadow: none !important;
    top: 0 !important;
}
/* Reset inside button groups */
[data-flux-button-group] [data-flux-button] {
    box-shadow: none !important;
    transform: none !important;
    top: 0 !important;
}
